.print_wrap         {background-color:#ffffff5e; width:calc(50% - 15px); border-left:1px solid #63a7ff; border-bottom:0; padding:4em 6em; margin-bottom:2em; box-shadow:3px 3px 3px #e1c5c51c;}
.print_comment      {margin-top:20px;}
.print_comment .comment_wrap{width:100%;}
.print_comment .comment     {background-color:transparent; padding:0; box-shadow:0;}
.print_comment .comment .comment_name {font-size:22px; line-height:24px;}
.print_comment .comment .comment_text {font-size:22px; line-height:24px; margin-top:10px;}
.print_info_wrap    {margin-bottom:20px; flex-wrap:wrap; justify-content:space-evenly; display:flex; width:100%; align-items:center;}
.print_info_text    {font-size:26px; line-height:30px; width:70%;}

@media (min-width: 360px) and (max-width: 767.98px) {
html            {font-size:18px;}
body            {padding:0.5em 1.5em; font-size:1.0rem;}
.head_content   {display:none;}
.mob_content    {display:block;}
.main_content   {padding-top:40px;}
.blog_wrap      {padding:60px 20px 0 20px;}
.blog_title     {font-size:2rem; line-height:inherit;}
.blog_author,
.blog_text p,
.blog_text p.intro  {font-size:1.0rem; line-height:normal; letter-spacing:inherit;}
h2              {font-size:1.2rem; line-height:inherit;}
.type,
.more,
.comment_text,
.text span,
.about_text     {font-size:1.0rem; line-height:inherit;}
.text span      {letter-spacing:-1px;}
h1,
.comment_name,
.text           {font-size:1.5rem; line-height:inherit;}
.box2 .type     {height:20px;}
.question       {width:100%;}
textarea#add_post::placeholder,
input#add_name::placeholder,
textarea#add_post,
input#add_name  {font-size:1.2rem; line-height:inherit;}
.post_link_text {font-size:2rem; line-height:inherit; padding:15px 5px;}
.pillar_wrap    {font-size:2.5rem; line-height:inherit;}
.wide .blog     {display:flex; flex-flow:column;}
.blog .img,
.blog.box1 .img {height:150px;}
.current .blog .img,
.current .blog.box1 .img {height:300px;}
.text_wrap      {padding:20px;}
.comment_list_wrap,
.current .blog,
.info_wrap      {width:100%; flex-flow:column;}
.comment_list_wrap  {margin-top:40px;}
.info_wrap      {margin-top:0;}
input#add_url::placeholder,
input#add_url,
.comment_url_text,
.comment_url,
.funder_img span,
.item_text      {font-size:0.8rem;}
.comment_wrap:nth-of-type(2n)   {margin:initial; margin-bottom:30px;}
.comment_wrap   {width:100%;}
.post_link_wrap {min-width:initial;}
.post_wrap      {width:100%; margin-top:40px;}
.post_area      {padding:20px;}
.post_more      {flex-flow:column;}
.post_name      {padding:0;}
.post_foot_wrap {align-items:center; flex-flow:column-reverse;}
.post_info      {margin-top:20px; padding-right:0; padding-left:0;}
input#add_submit{margin-top:20px; width:100%;}
.cc_wrap        {display:block;}
.cc_text,
.support,
.code           {margin-bottom:20px;}
.about_text_zine  {text-align:initial;}
.funder_wrap    {flex-flow:column;}
.funder_img     {width:initial;}
.funder_text    {padding:20px 0 0 0;}
a.print_link    {display:none;}
a.mob_print_link{display:block;}
}

@media (min-width: 768px) and (max-width: 991.98px) {
body            {padding:0.5em 1.5em; font-size:1.0rem;}
.nav_content    {padding:0;}
.blog_wrap      {padding:40px;}
.wide .blog .img{height:initial;}
.blog.full .img,
.blog.box1 .img {height:150px;}
h1, h2,
.comment_name   {font-size:1.5rem; line-height:inherit;}
.text           {font-size:1.8rem; line-height:inherit;}
.about_text     {font-size:1.0rem; line-height:inherit;}
.text span      {font-size:1.0rem; letter-spacing:-1px;}
.box2 .type     {height:40px;}
a.cc_link       {display:inline;}
.funder_img span,
.cc_wrap > div  {font-size:0.8em;}
.cc_text        {width:85%;}
.comment_list_wrap      {width:100%;}
.info_wrap      {width:100%; flex-flow:wrap;}
.post_foot_wrap {align-items:flex-start;}
.post_info      {margin-top:20px;padding-right:20px; padding-left:0;}
.funder_wrap    {flex-flow:wrap; justify-content:left;}
.funder_text    {padding:20px 0 0 0;}
.support        {flex-flow:wrap;}
}

@media print {
@page           {size:148mm 210mm; margin-top:15mm; margin-bottom:20mm;}
body            {background:#fff; color:#000; visibility:hidden;}
.main_wrap      {display:none;}
.print          {display:block; visibility:visible;}
.print_comment .comment .comment_name {font-size:16px; line-height:18px;}
.print_comment .comment .comment_text {font-size:18px; line-height:22px; margin-top:10px;}
.print_footer   {display:flex;}

}
